        private static char up = '(';

        public static int getResult()
        {
            char[] input = inputString();

            int i = 0;

            foreach(char c in input)
            {
                if (c == up)
                {
                    i += 1;
                }
                else
                {
                    i -= 1;
                }
            }

            return i;
        }

        public static int getSecondResult()
        {
            char[] input = inputString();

            int i = 0;
            int x = 0;

            foreach (char c in input)
            {
                if(i == -1)
                {
                    break;
                }

                x++;

                if (c == up)
                {
                    i += 1;
                }
                else
                {
                    i -= 1;
                }
            }

            return x;
        }

